INSERT INTO `!PREFIX!_content` VALUES(147, 65, 1, 1, '<div>\r\n<div>\r\n<div>\r\n<p>Weil wir Nachhaltigkeit seit &uuml;ber 10 Jahren leben</p>\r\n</div>\r\n</div>\r\n</div>', '', 'sysadmin', '2013-02-13 09:20:41', '2013-02-22 12:26:59');
INSERT INTO `!PREFIX!_content` VALUES(148, 65, 1, 2, '<p>sind wir Ihr CONTENIDO Implementierer.</p>', '', 'sysadmin', '2013-02-13 09:20:48', '2013-02-13 09:20:48');
INSERT INTO `!PREFIX!_content` VALUES(149, 65, 2, 1, '<div>\r\n<div>\r\n<div>\r\n<p>Ob Internet, Extranet, Intranets oder die Entwicklung von PlugIns - Sie erhalten den zu Ihnen passenden Onlineauftritt mit einem Esprit, der Sie und ihre Kunden begeistern wird. Sprechen Sie mit uns &uuml;ber Ihre Herausforderungen und wir unterbreiten Ihnen dazu gerne individuelle L&ouml;sungsvorschl&auml;ge.</p>\r\n<p>Seit mehr als 13 Jahren implementieren wir CONTENIDO und optimieren es entlang der Bed&uuml;rfnisse unserer Kunden. Wir entwickeln Schnittstellen und Konnektoren, beraten beim Aufbau und der Umsetzung und schaffen das richtige Interface f&uuml;r die Botschaft an ihre Kunden.</p>\r\n<p>Stellen Sie uns auf die Probe und lassen Sie sich &uuml;berzeugen.</p>\r\n<p>four for business AG<br /> +49 69 80 10 82-0 | info@4fb.de | www.4fb.de</p>\r\n</div>\r\n</div>\r\n</div>', '', 'sysadmin', '2013-02-13 09:21:06', '2013-02-22 12:28:12');
