INSERT INTO `!PREFIX!_content` VALUES(152, 66, 2, 1, '<div>\r\n<div>\r\n<div>\r\n<p>Lassen Sie ihr bestehendes System aktualisieren. Die Spezialisten der four for business AG verf&uuml;gen &uuml;ber 13 Jahre CONTENIDO Know How. Wir gew&auml;hrleisten eine reibungslose und preiswerte Umstellung Ihres Systems. Lassen Sie sich ein Angebot erstellen - Es lohnt sich.</p>\r\n<p>Sprechen Sie uns an!</p>\r\n<p>four for business AG</p>\r\n<p>+49 69 801082-0 | info@4fb.de | www.4fb.de</p>\r\n</div>\r\n</div>\r\n</div>', '', 'sysadmin', '2013-02-13 09:25:27', '2013-02-22 12:28:54');
INSERT INTO `!PREFIX!_content` VALUES(153, 62, 21, 1, '<?xml version="1.0" encoding="utf-8"?>\n<filelist><title></title><style>cms_filelist_style_default.html</style><directories><array_value>downloads</array_value></directories><incl_subdirectories>false</incl_subdirectories><manual>false</manual><sort>filename</sort><incl_metadata>true</incl_metadata><extensions></extensions><sortorder>asc</sortorder><filesizefilter_from>0</filesizefilter_from><filesizefilter_to>0</filesizefilter_to><ignore_extensions>true</ignore_extensions><manual_files></manual_files><filecount>0</filecount><md_description_limit>0</md_description_limit><md_medianame_limit>0</md_medianame_limit><md_copyright_limit>0</md_copyright_limit><md_keywords_limit>0</md_keywords_limit><md_internal_notice_limit>0</md_internal_notice_limit><creationdatefilter_from>0</creationdatefilter_from><creationdatefilter_to>0</creationdatefilter_to><modifydatefilter_from>0</modifydatefilter_from><modifydatefilter_to>0</modifydatefilter_to></filelist>\n', '', 'sysadmin', '2013-02-13 09:28:56', '2013-02-18 08:55:35');
INSERT INTO `!PREFIX!_content` VALUES(154, 67, 1, 1, '<p>Einfach benutzen.</p>', '', 'sysadmin', '2013-02-13 09:47:37', '2013-02-13 09:47:37');
INSERT INTO `!PREFIX!_content` VALUES(155, 67, 2, 1, '<p class="FreieForm">Der Verwaltungsbereich von CONTENIDO wurde so konzipiert, dass Benutzer ohne umfassende Kenntnisse in der Lage sind, Inhalte von Onlinediensten einfach zu erstellen, zu pflegen und zu verwalten. &Uuml;ber die Insite-Editing-Funktionalit&auml;t von CONTENIDO ist es m&ouml;glich, Inhalte direkt in dem sp&auml;teren Layout der Website zu bearbeiten.</p>', '', 'sysadmin', '2013-02-13 09:48:38', '2013-02-13 10:08:46');
INSERT INTO `!PREFIX!_content` VALUES(162, 67, 1, 2, '<p>So einfach und intuitiv wie Sie es sonst gewohnt sind zu arbeiten, l&auml;sst sich CONTENIDO benutzen.</p>', '', 'sysadmin', '2013-02-13 10:08:52', '2013-02-13 10:08:52');
