INSERT INTO `!PREFIX!_content` VALUES(223, 51, 2, 604, '<div>\r\n<p><strong>four for business AG</strong></p>\r\n<p>Nordring 82B<br />63067 Offenbach</p>\r\n<p>Telefon: +49 69 801082-0<br />E-Mail: info@contenido.org<br />Internet: http://www.4fb.de</p>\r\n</div>', '', 'sysadmin', '2013-02-14 14:34:46', '2013-02-14 14:34:46');
INSERT INTO `!PREFIX!_content` VALUES(224, 51, 2, 605, '<div>\r\n<h1><span style="color: #007abe;">Wie Sie uns finden</span></h1>\r\n<p><img src="http://www.4fb.de/images/spacer.gif" border="0" alt="" width="1" height="10" /></p>\r\n<h2>mit dem Auto oder der Bahn.</h2>\r\n<p><img src="http://www.4fb.de/images/spacer.gif" border="0" alt="" width="1" height="10" /></p>\r\n<p>Ab dem 01. Juni 2008 erreichen Sie uns</p>\r\n<div>&nbsp;</div>\r\n<div>per Auto von Frankfurt:</div>\r\n<div>&nbsp;</div>\r\n<div>Von der Hanauer Landstrasse oder dem Riederwald auf die A661 fahren Richtung Offenbach. Ausfahrt Offenbach-Kaiserlei nehmen und im Kreisel die vierte Ausfahrt Richtung Hafen / Messe / Heyne Fabrik fahren.&nbsp;An der ersten Kreuzung nach links abbiegen und dem Strassenverlauf folgen. Auf dem Nordring an der Lilistrasse (rechte Seite vorbeifahren) und danach in das zweite Tor einbiegen. Im Hof der Heyne Fabrik finden Sie gleich links einen kleinen Platz, mit dem Eingang (Haus 4) zur 82 B.</div>\r\n<div>&nbsp;</div>\r\n<div>per Auto von K&ouml;ln / W&uuml;rzburg:</div>\r\n<div>&nbsp;</div>\r\n<div>Von der A3 auf die A661 Richtung Frankfurt/Offenbach.</div>\r\n<div>Ausfahrt Offenbach-Kaiserlei, nach der Ausfahrt im Kreisel die zweite Strasse rechts ab und dann der Beschreibung ''Von Frankfurt'' folgen.&nbsp;\r\n<div>&nbsp;</div>\r\n</div>\r\n<div>&nbsp;</div>\r\n<div>per Bahn von Frankfurt Hauptbahnhof:</div>\r\n<div>&nbsp;</div>\r\n<div>Ab Frankfurt Hauptbahnhof tief Gleis 102 mit der S-Bahn Linie S1 bis zur Haltestelle Offenbach Ledermuseum. Danach laufen Sie die Lilistra&szlig;e Richtung Main und biegen dann rechts ab auf den Nordring. Durch die zweite gro&szlig;e Toreinfahrt kommen sie zur Heyne Fabrik. Im Hof finden Sie gleich links einen kleinen Platz mit dem Eingang (Haus 4) zur 82 B.</div>\r\n<div>&nbsp;</div>\r\n<div>Wir w&uuml;nschen Ihnen eine gute Fahrt.</div>\r\n</div>', '', 'sysadmin', '2013-02-14 14:35:11', '2013-02-14 14:35:11');
