INSERT INTO `!PREFIX!_content` VALUES(227, 78, 1, 1, '<p>The Philosophy of CONTENIDO</p>', '', 'sysadmin', '2012-11-11 04:18:19', '2013-02-20 08:55:29');
INSERT INTO `!PREFIX!_content` VALUES(228, 78, 1, 2, '<p>&bdquo;Simplicity is a result of maturity.&rdquo;&nbsp; Friedrich Schiller</p>', '', 'sysadmin', '2012-11-11 04:18:19', '2013-02-20 08:53:39');
INSERT INTO `!PREFIX!_content` VALUES(229, 78, 2, 1, '<p><strong>Background and Philosophy</strong><strong></strong></p>\r\n<p>The Open Source Content Management System CONTENIDO results from over 13 years of experience in building web based software. The guiding philosophy has been to attain simplicity through the elimination of complexity.</p>\r\n<p>CONTENIDO has proven itself to be a reliable choice for Internet, Extranet and Intranet solutions, mobile web sites and content syndication or as a content provider for e-commerce systems. Day to day it serves a broad variety of application areas at SMEs and well-know companies. CONTENIDO belongs to the leading German Open Source CMS.</p>\r\n<p>Simple ease of use and ground up modularity characterise this system.</p>\r\n<p>Our principles: <a href="front_content.php?idart=66">Just Publish</a>, <a href="front_content.php?idart=67">Just Unlimited</a>, <a href="front_content.php?idart=68">Just Simple</a></p>', '', 'sysadmin', '2012-11-11 04:18:19', '2013-02-21 17:26:52');
