INSERT INTO `!PREFIX!_content` VALUES(284, 86, 1, 2, '<p>Discover the features of the CONTENIDO sample site.</p>', '', 'sysadmin', '2013-02-14 09:49:51', '2013-02-21 12:37:45');
INSERT INTO `!PREFIX!_content` VALUES(285, 86, 2, 1, '<p>When installing CONTENIDO this sample site can be automatically installed as well.&nbsp; This will help you to easily learn and understand the content management system. The sample site already contains a variety of features and plugins.</p>', '', 'sysadmin', '2013-02-11 16:34:36', '2013-02-21 12:37:33');
INSERT INTO `!PREFIX!_content` VALUES(286, 87, 1, 1, '<p>Navigation</p>', '', 'sysadmin', '2012-11-11 05:14:20', '2012-11-11 05:14:20');
INSERT INTO `!PREFIX!_content` VALUES(287, 87, 2, 1, '<p>Navigation on this website consists of three parts:</p>\r\n<ol>\r\n<li>Main navigation for a content navigation on the left side of the website.</li>\r\n<li>Header navigation for navigation in the upper area of the website. You can find there language switch, contact, search box and newsletter registration.</li>\r\n<li>Service navigation displays navigation elements in the lower area of the website, for imprint, links to CONTENIDO forum and FAQ.</li>\r\n</ol>', '', 'sysadmin', '2013-02-11 16:39:00', '2013-02-21 17:36:48');
INSERT INTO `!PREFIX!_content` VALUES(288, 88, 1, 1, '<p>Content</p>', '', 'sysadmin', '2012-11-11 05:16:02', '2013-02-11 17:08:09');
INSERT INTO `!PREFIX!_content` VALUES(289, 88, 2, 1, '<p>Content in CONTENIDO not only consists of text and image elements but also of content teaser, picture galleries, links list etc.</p>\r\n<p>With a broad variety of features you can present perfectly your content.</p>', '', 'sysadmin', '2013-02-11 17:08:56', '2013-02-21 17:39:51');
