INSERT INTO `!PREFIX!_content` (`idcontent`, `idartlang`, `idtype`, `typeid`, `value`, `version`, `author`, `created`, `lastmodified`) VALUES(123, 62, 2, 1, '<p>Haben Sie Preislisten, Produktdatenbl&auml;tter, Bildmaterial oder andere Informationen, die Sie den Besuchern Ihrer Website zum Download zur Verf&uuml;gung stellen wollen? CONTENIDO liefert Ihnen auch daf&uuml;r ein fertiges Modul, das alle Dateien eines ausgew&auml;hlten Verzeichnisses inklusive Datentyp und Dateigr&ouml;&szlig;e ausliest und in Form einer Liste darstellt.</p>', '', 'sysadmin', NOW(), NOW());
INSERT INTO `!PREFIX!_content` (`idcontent`, `idartlang`, `idtype`, `typeid`, `value`, `version`, `author`, `created`, `lastmodified`) VALUES(124, 33, 2, 1, '<p>Ein H&auml;ndlerbereich mit Informationsmaterial? Eine geschlossene Presselounge? Oder ganz allgemein formuliert: Ein Ort, an dem Sie Informationen nur ausgew&auml;hlten Besuchern Ihrer Website zur Verf&uuml;gung stellen wollen? Nutzen Sie den geschlossenen Bereich von CONTENIDO um einzelne Bereiche Ihres Webauftritts mit einem Passwort zu sch&uuml;tzen.</p>\r\n<p class="FreieForm">CONTENIDO verf&uuml;gt &uuml;ber eine Frontend- und Backendverwaltung f&uuml;r die Benutzergruppen. So kann die redaktionelle Benutzergruppe (Backendzugriff) einfach von einer Zugriffsberechtigung f&uuml;r Kundengruppen bzw. Frontendbenutzer getrennt werden.<strong><br /></strong></p>\r\n<p></p>\r\n<p></p>', '', 'sysadmin', NOW(), NOW());
