INSERT INTO `!PREFIX!_content` (`idcontent`, `idartlang`, `idtype`, `typeid`, `value`, `version`, `author`, `created`, `lastmodified`) VALUES(152, 66, 2, 1, '<div>\r\n<div>\r\n<div>\r\n<p>Lassen Sie ihr bestehendes System aktualisieren. Die Spezialisten der four for business AG verf&uuml;gen &uuml;ber 13 Jahre CONTENIDO Know-how. Wir gew&auml;hrleisten eine reibungslose und preiswerte Umstellung Ihres Systems. Lassen Sie sich ein Angebot erstellen - es lohnt sich.</p>\r\n<p>Sprechen Sie uns an!</p>\r\n<p>four for business AG</p>\r\n<p> 49 (0) 69 801082-0 | info@4fb.de | www.4fb.de</p>\r\n</div>\r\n</div>\r\n</div>', '', 'sysadmin', NOW(), NOW());
