INSERT INTO `!PREFIX!_content` (`idcontent`, `idartlang`, `idtype`, `typeid`, `value`, `version`, `author`, `created`, `lastmodified`) VALUES(443, 127, 2, 1, '<p>CONTENIDO bietet eine eingebaute Unters&uuml;tzung f&uuml;r verschiedene Social Media Plattformen.</p>\r\n<p>Es gibt vier einfach konfigurierbare Module f&uuml;r einen XING Link, den Google "+1" Button, den Facebook "Like" Button und die eingebunden Timelines von Twitter.</p>\r\n<p>Sie finden zu jedem genannten ein Beispiel in dieser Kategorie.</p>', '', 'sysadmin', NOW(), NOW());
