INSERT INTO `!PREFIX!_content` (`idcontent`, `idartlang`, `idtype`, `typeid`, `value`, `version`, `author`, `created`, `lastmodified`) VALUES(432, 126, 2, 1, '<p>The Twitter module supports the new Embedded Timelines (and is fully API v1.1 compatible).</p>\r\n<p>You can customize this module to show different Twitter accounts, choose between a dark and a light theme and even change the color of links in the Timeline.</p>\r\n<p>If a user replies or retweets a Tweet from this Timeline they will be presented with a list of accounts to follow.</p>', '', 'sysadmin', NOW(), NOW());
