INSERT INTO `!PREFIX!_content` (`idcontent`, `idartlang`, `idtype`, `typeid`, `value`, `version`, `author`, `created`, `lastmodified`) VALUES(265, 82, 2, 1, '<p>CONTENIDO has been a Open-Source-CMS Provider for more than 13 years. Whether inter-, extra- or intranet, mobile websites, content-syndication or as a content provider for e-commerce solutions <span><span>the system</span> <span class="hps">is used</span> <span class="hps">in many installations</span> <span class="hps">by renowned companies</span><span>.</span></span></p>', '', 'sysadmin', NOW(), NOW());
