INSERT INTO `!PREFIX!_content` (`idcontent`, `idartlang`, `idtype`, `typeid`, `value`, `version`, `author`, `created`, `lastmodified`) VALUES(270, 83, 1, 1, '<p>Content</p>', '', 'sysadmin', NOW(), NOW());
INSERT INTO `!PREFIX!_content` (`idcontent`, `idartlang`, `idtype`, `typeid`, `value`, `version`, `author`, `created`, `lastmodified`) VALUES(271, 83, 2, 1, '<ul>\r\n<li>\r\n<p><strong>Teaser- and Snippet Functionality: </strong><strong></strong>CONTENIDO presents various options for creating and presenting content teasers and lists of articles, e.g. for overview and landing pages.</p>\r\n</li>\r\n</ul>\r\n<p></p>\r\n<ul>\r\n<li>\r\n<p><strong>Synchronize Multilingual Content</strong><strong></strong>: CONTENIDO eases the multilingual editorial life. Select a language - synchronize - translate - and just publish. There is no restriction on the number of languages you can use.</p>\r\n<p class="FreieForm"></p>\r\n</li>\r\n</ul>\r\n<p></p>\r\n<ul>\r\n<li>\r\n<p class="FreieForm"><strong>Social Media</strong><strong></strong> Social Media modules easily integrate services like Twitter, Facebook, Google , LinkedIn and XING.</p>\r\n</li>\r\n</ul>\r\n<p></p>\r\n<ul>\r\n<li>\r\n<p><strong>Time-Controlled Publishing</strong><strong></strong>: Time control allows you publishing your content whenever it suits you. This mechanism covers media resources such as images and documents as well.</p>\r\n</li>\r\n</ul>\r\n<p></p>\r\n<ul>\r\n<li>\r\n<p class="FreieForm"><strong>Version Control - Database Level</strong>: CONTENIDO applies version control to layouts, modules and content related information. Further version control enhancements are scheduled for 2013.</p>\r\n</li>\r\n</ul>\r\n<p></p>\r\n<ul>\r\n<li>\r\n<p><strong>Link Management: </strong><strong></strong>CONTENIDO&acute;s integrated link checking mechanism verifies the reachability of internal and external links of your online service.</p>\r\n</li>\r\n</ul>\r\n<p></p>\r\n<ul>\r\n<li>\r\n<p><strong>User Comments, Blog and Bulletin Board</strong><strong></strong>: CONTENIDO offers the ability to increase user feedback by adding a comment and rating option to specific pages - integrating your own blog.</p>\r\n<p>The addition of a bulletin board enhances your online service with real user generated content.</p>\r\n<p class="FreieForm"><strong>&nbsp;</strong></p>\r\n</li>\r\n</ul>', '', 'sysadmin', NOW(), NOW());
