INSERT INTO `!PREFIX!_content` (`idcontent`, `idartlang`, `idtype`, `typeid`, `value`, `version`, `author`, `created`, `lastmodified`) VALUES(284, 86, 1, 2, '<p>Discover the features of the CONTENIDO sample site.</p>', '', 'sysadmin', NOW(), NOW());
INSERT INTO `!PREFIX!_content` (`idcontent`, `idartlang`, `idtype`, `typeid`, `value`, `version`, `author`, `created`, `lastmodified`) VALUES(285, 86, 2, 1, '<p>When installing CONTENIDO this sample site can be automatically installed as well.&nbsp; This will help you to easily learn and understand the content management system. The sample site already contains a variety of features and plugins.</p>', '', 'sysadmin', NOW(), NOW());
INSERT INTO `!PREFIX!_content` (`idcontent`, `idartlang`, `idtype`, `typeid`, `value`, `version`, `author`, `created`, `lastmodified`) VALUES(286, 87, 1, 1, '<p>Navigation</p>', '', 'sysadmin', NOW(), NOW());
INSERT INTO `!PREFIX!_content` (`idcontent`, `idartlang`, `idtype`, `typeid`, `value`, `version`, `author`, `created`, `lastmodified`) VALUES(287, 87, 2, 1, '<p>Navigation on this website consists of three parts:</p>\r\n<ol>\r\n<li>Main navigation for a content navigation on the left side of the website.</li>\r\n<li>Header navigation for navigation in the upper area of the website. You can find there language switch, contact, search box and newsletter registration.</li>\r\n<li>Service navigation displays navigation elements in the lower area of the website, for imprint, links to CONTENIDO forum and FAQ.</li>\r\n</ol>', '', 'sysadmin', NOW(), NOW());
