INSERT INTO `!PREFIX!_content` (`idcontent`, `idartlang`, `idtype`, `typeid`, `value`, `version`, `author`, `created`, `lastmodified`) VALUES(462, 129, 2, 1, '<p>Das Twitter Modul unterst&uuml;tzt die neue Embedded Timeline (und ist damit voll kompatibel zur neuen API v1.1).</p>\r\n<p>Folgende Einstellungen k&ouml;nnen Sie vornehmen:</p>\r\n<ul>\r\n<li>Anzeige der Timeline</li>\r\n<li>heller oder dunkle Stil f&uuml;r die Darstellung</li>\r\n<li>Anpassung der Farben an die Ihrer Webseite</li>\r\n</ul>\r\n<p>Antwortet oder Retweeted ein Benutzer etwas aus dieser Timeline wird ihm eine Liste von Accounts zum Folgen vorgeschlagen.</p>', '', 'sysadmin', NOW(), NOW());
