CREATE TABLE `!PREFIX!_pifa_field` (`idfield` int(10) unsigned NOT NULL auto_increment COMMENT 'unique identifier for a ConForm field',`idform` int(10) unsigned NOT NULL default '0' COMMENT 'foreign key for the ConForm form',`field_rank` int(10) unsigned NOT NULL default '0' COMMENT 'rank of a field in a form',`field_type` int(10) unsigned NOT NULL default '0' COMMENT 'id which defines type of form field',`column_name` varchar(64) NOT NULL COMMENT 'name of data table column to store values',`label` varchar(1023) default NULL COMMENT 'label to be shown in frontend',`display_label` int(1) NOT NULL default '0' COMMENT '1 means that the label will be displayed',`default_value` varchar(1023) default NULL COMMENT 'default value to be shown for form field',`option_labels` varchar(1023) default NULL COMMENT 'CSV of option labels',`option_values` varchar(1023) default NULL COMMENT 'CSV of option values',`option_class` varchar(1023) default NULL COMMENT 'class implementing external datasource',`help_text` text COMMENT 'help text to be shown for form field',`obligatory` int(1) NOT NULL default '0' COMMENT '1 means that a value is obligatory',`rule` varchar(1023) default NULL COMMENT 'regular expression to validate value',`error_message` varchar(1023) default NULL COMMENT 'error message to be shown for an invalid value',`css_class` varchar(1023) default NULL COMMENT 'CSS classes to be used for field wrapper',PRIMARY KEY  (`idfield`)) ENGINE=MyISAM COMMENT='contains meta data of PIFA fields' AUTO_INCREMENT=1 ;


CREATE TABLE `!PREFIX!_pifa_form` (`idform` int(10) unsigned NOT NULL auto_increment COMMENT 'unique identifier for a ConForm form',`idclient` int(10) unsigned NOT NULL default '0' COMMENT 'id of form client',`idlang` int(10) unsigned NOT NULL default '0' COMMENT 'id of form language',`name` varchar(1023) NOT NULL default 'new form' COMMENT 'human readable name of form',`data_table` varchar(64) NOT NULL default '!PREFIX!_pifo_data' COMMENT 'unique name of data table',`method` enum('get','post') NOT NULL default 'post' COMMENT 'method to be used for form submission', with_timestamp BOOLEAN NOT NULL DEFAULT '1' COMMENT 'if data table records have a timestamp', PRIMARY KEY (`idform`)) ENGINE=MyISAM COMMENT='contains meta data of PIFA forms' AUTO_INCREMENT=1 ;

